mapper.select:
select ld.ship_id depositNo ,ld.ship_seq depositSeq ,concat(ld.ship_id ,'-',ld.ship_seq) depositKey ,ld.assort_id assortId ,ld.item_id itemId ,lm.receipt_dt depositDt ,tod.order_id orderId ,tod.order_seq orderSeq ,concat(tod.order_id ,'-',tod.order_seq) orderKey ,tod.deli_method deliMethod ,tod.qty qty ,tod.storage_id orderStorageId ,it.assort_nm assortNm , (select b1.option_nm from ititmm b2, itvari b1 where b2.assort_id=tod.assort_id And b2.item_id= tod.item_id And b2.assort_id = b1.assort_id and b2.variation_gb1 = b1.variation_gb and b2.variation_seq1=b1.seq) optionNm1 , (select b1.option_nm from ititmm b2, itvari b1 where b2.assort_id=tod.assort_id And b2.item_id= tod.item_id And b2.assort_id = b1.assort_id and b2.variation_gb2 = b1.variation_gb and b2.variation_seq2=b1.seq) optionNm2 from lsshpm lm ,lsshpd ld ,itasrt it ,tb_order_detail tod where 1=1 and lm.receipt_dt between #{startDt} and #{endDt} and lm.ship_id = ld.ship_id and ld.assort_id = it.assort_id and ld.order_id = tod.order_id and ld.order_seq = tod.order_seq and tod.status_cd ='C01'
and lm.storage_id =#{storageId}
and it.assort_id =#{assortId}
and it.assort_nm like CONCAT( '%', #{assortNm}, '%')
and tod.deli_method =#{deliMethod}
