mapper.select:
select dd.*, dd.cust_nm cust_nm, dd.cust_tel cust_tel from (select aa.channel_gb channel_gb, aa.order_date, aa.order_id, aa.order_name cust_nm, aa.order_tel cust_tel, aa.cust_id cust_id, bb.status_cd, bb.assort_id, bb.goods_nm, bb.option_info, bb.qty, bb.sale_price, bb.deli_price, bb.dc_sum_price, aa.order_status, (bb.sale_price + bb.deli_price - bb.dc_sum_price) total_price from tb_order_master aa, tb_order_detail bb, tb_order_member_address cc where 1 = 1 and aa.order_id = bb.order_id and aa.deli_id = aa.deli_id and aa.order_date between #{orderFromDt} and #{orderEndDt} and aa.channel_gb like CONCAT(#{channelGb}, '%')
and aa.channel_order_no = #{channelOrderNo}
and bb.status_cd = #{statusCd}
and bb.assort_id = #{assortId}
) dd left join tb_member cc on cc.cust_id = dd.cust_id;
and cc.cust_nm like CONCAT(#{custNm},'%' )
and cc.cust_tel = #{custTel}
and cc.cust_hp = #{custHp}

select aa.channel_gb ,aa.order_date ,aa.order_id ,bb.status_cd ,dd.deli_name ,dd.deli_tel ,bb.assort_id ,bb.goods_nm ,bb.option_info ,bb.qty ,bb.sale_price ,bb.deli_price ,bb.dc_sum_price ,(bb.sale_price + bb.deli_price - bb.dc_sum_price) total_price from tb_order_master aa ,tb_order_detail bb ,tb_member cc ,tb_member_address dd where 1=1 and aa.order_id = bb.order_id and aa.cust_id = cc.cust_id and aa.deli_id = dd.deli_id and aa.order_date between STR_TO_DATE(#{orderFromDt}, '%Y-%m-%d %H:%i:%s') and STR_TO_DATE(#{orderEndDt}, '%Y-%m-%d %H:%i:%s') and aa.channel_gb like CONCAT(#{channelGb},'%' )
and aa.channel_order_no = #{channelOrderNo}
and bb.status_cd = #{statusCd}
and bb.assort_id = #{assortId}
and dd.deli_name like CONCAT(#{custNm},'%' )
and dd.deli_tel = #{custTel}
and aa.order_hp = #{custHp}

select tom.channel_gb channelGb ,tom.order_date orderDate ,tom.order_id orderId ,tod.order_seq orderSeq ,case when SUBSTR(order_status FROM 1 FOR 1) in ('s','d','g','p') then '결제완료' when SUBSTR(order_status FROM 1 FOR 1) ='o' then '결제전' else '결제실패 및 취소' end payStatus ,tom.channel_order_no channelOrderNo ,tod.channel_order_seq channelOrderSeq ,tom.cust_id custId ,tom.order_name custNm ,tom.order_hp custHp ,tom.order_tel custTel ,tod.goods_nm goodsNm ,ita.assort_id assortId ,itm.item_id itemId ,tod.status_cd orderStatus ,(select iv.option_nm from itvari iv where iv.assort_id = itm.assort_id and iv.seq = itm.variation_seq1 and iv.variation_gb='01' and del_yn='02' ) optionNm1 ,(select iv.option_nm from itvari iv where iv.assort_id = itm.assort_id and iv.seq = itm.variation_seq2 and iv.variation_gb='02' and del_yn='02' ) optionNm2 ,(select iv.option_nm from itvari iv where iv.assort_id = itm.assort_id and iv.seq = itm.variation_seq3 and iv.variation_gb='03' and del_yn='03' ) optionNm3 ,tod.qty qty ,tod.goods_price goodsPrice ,tod.deli_price deliPrice ,tod.deli_method deliMethod ,tod.delivery_info deliveryInfo ,tod.scm_no scmNo ,tom.total_goods_price totalGoodsPrice ,tom.total_delivery_charge totalDeliveryCharge ,tom.total_goods_price + tom.total_delivery_charge totalPrice ,tom.order_amt orderAmt ,tom.pay_gb payGb ,tom.pay_dt payDt -- ,tma.deli_nm deliNm ,tma.deli_nm deliNm ,tma.deli_hp deliHp ,tma.deli_tel deliTel ,tma.deli_addr1 deliAddr from tb_order_detail tod inner join tb_order_master as tom on tom.order_id = tod.order_id LEFT OUTER JOIN itasrt as ita on tod.assort_id = ita.assort_id LEFT OUTER JOIN ititmm as itm on tod.assort_id = itm.assort_id and tod.item_id = itm.item_id LEFT OUTER JOIN tb_member AS tm -- A_TABLE 기준 ON tom.cust_id = tm.cust_id LEFT OUTER JOIN tb_member_address AS tma -- A_TABLE 기준 ON tom.deli_id = tma.deli_id where 1=1 and tom.order_date between #{startDt} and #{endDt}
and tom.order_id =#{orderId}
and tod.status_cd =#{orderStatus}
and tom.order_name like CONCAT(#{custNm},'%' )
and tom.order_hp = #{custHp}
and tom.order_tel = #{custTel}
and tma.deli_nm = #{deliNm}
and tma.deli_hp = #{deliHp}
and tma.deli_tel = #{deliTel}
and tom.channel_order_no =#{channelOrderNo}
and tod.assort_id =#{assortId}
and ita.assort_nm like concat('%',#{assortNm},'%')

select tom.channel_gb channelGb ,tom.order_date orderDate ,tom.order_id orderId ,tod.order_seq orderSeq ,case when SUBSTR(order_status FROM 1 FOR 1) in ('s','d','g','p') then '결제완료' when SUBSTR(order_status FROM 1 FOR 1) ='o' then '결제전' else '결제실패 및 취소' end payStatus ,tom.channel_order_no channelOrderNo ,tod.channel_order_seq channelOrderSeq ,tm.cust_id custId ,tom.order_name custNm ,tom.order_tel custHp ,tom.order_tel custTel ,tod.goods_nm goodsNm ,ita.assort_id assortId ,itm.item_id itemId ,tod.status_cd orderStatus ,(select iv.option_nm from itvari iv where iv.assort_id = itm.assort_id and iv.seq = itm.variation_seq1 and iv.variation_gb='01' and del_yn='02' ) optionNm1 ,(select iv.option_nm from itvari iv where iv.assort_id = itm.assort_id and iv.seq = itm.variation_seq2 and iv.variation_gb='02' and del_yn='02' ) optionNm2 ,(select iv.option_nm from itvari iv where iv.assort_id = itm.assort_id and iv.seq = itm.variation_seq2 and iv.variation_gb='03' and del_yn='03' ) optionNm3 ,tod.qty qty ,tod.goods_price goodsPrice ,tod.deli_price deliPrice ,tod.deli_method deliMethod ,tod.delivery_info deliveryInfo ,tod.scm_no scmNo ,tom.total_goods_price totalGoodsPrice ,tom.total_delivery_charge totalDeliveryCharge ,tom.total_goods_price + tom.total_delivery_charge totalPrice ,tom.order_amt orderAmt ,tom.pay_gb payGb ,tom.pay_dt payDt ,tma.deli_nm deliNm ,tma.deli_hp deliHp ,tma.deli_tel deliTel ,tma.deli_addr1 deliAddr from tb_order_detail tod inner join tb_order_master as tom on tom.order_id = tod.order_id LEFT OUTER JOIN itasrt as ita on tod.assort_id = ita.assort_id LEFT OUTER JOIN ititmm as itm on tod.assort_id = itm.assort_id and tod.item_id = itm.item_id LEFT OUTER JOIN tb_member AS tm -- A_TABLE 기준 ON tom.cust_id = tm.cust_id LEFT OUTER JOIN tb_member_address AS tma -- A_TABLE 기준 ON tom.deli_id = tma.deli_id where 1=1 and tom.order_date between #{startDt} and #{endDt}
and tom.order_id =#{orderId}
and tod.status_cd =#{orderStatus}
and tom.order_name like CONCAT(#{custNm},'%' )
and tom.order_tel = #{custHp}
and tom.order_tel = #{custTel}
and tma.deli_nm = #{deliNm}
and tma.deli_hp = #{deliHp}
and tma.deli_tel = #{deliTel}
and tom.channel_order_no =#{channelOrderNo}

select aa.channel_gb channelGb ,aa.order_date orderDate ,bb.order_id orderId ,bb.order_seq orderSeq ,concat(bb.order_id,'-',bb.order_seq) orderKey ,bb.status_cd statusCd ,aa.order_name custNm ,bb.assort_id assortId ,bb.item_id itemId ,bb.goods_nm goodsNm , (select b1.option_nm from ititmm b2, itvari b1 where b2.assort_id=bb.assort_id And b2.item_id= bb.item_id And b2.assort_id = b1.assort_id and b2.variation_gb1 = b1.variation_gb and b2.variation_seq1=b1.seq) optionNm1 , (select b1.option_nm from ititmm b2, itvari b1 where b2.assort_id=bb.assort_id And b2.item_id= bb.item_id And b2.assort_id = b1.assort_id and b2.variation_gb2 = b1.variation_gb and b2.variation_seq2=b1.seq) optionNm2 , (select b1.option_nm from ititmm b2, itvari b1 where b2.assort_id=bb.assort_id And b2.item_id= bb.item_id And b2.assort_id = b1.assort_id and b2.variation_gb3 = b1.variation_gb and b2.variation_seq3=b1.seq) optionNm3 ,bb.option_info optionInfo ,bb.qty qty ,bb.sale_price salePrice ,bb.deli_price deliPrice ,bb.dc_sum_price dcSumPrice ,(bb.sale_price + bb.deli_price - bb.dc_sum_price) totalPrice ,bb.list_image_data listImageData ,bb.channel_order_no channelOrderNo ,bb.channel_order_seq channelOrderSeq from tb_order_master aa ,tb_order_detail bb where 1=1 and aa.order_id = bb.order_id and aa.order_date between #{startDt} and #{endDt}
and bb.status_cd =#{statusCd}
and aa.order_id =#{orderId}
and bb.channel_order_no =#{channelOrderNo}
