mapper.select:
select ld.ship_id shipId ,ld.ship_seq shipSeq ,concat(ld.ship_id ,'-',ld.ship_seq) shipKey ,ld.assort_id assortId ,ld.item_id itemId ,lm.receipt_dt receiptDt ,tom.order_date orderDt ,tod.order_id orderId ,tod.order_seq orderSeq ,concat(tod.order_id ,'-',tod.order_seq) orderKey ,tod.deli_method deliMethod ,tod.qty qty ,tod.storage_id orderStorageId ,it.assort_nm assortNm ,it.assort_gb assortGb , (select b1.option_nm from ititmm b2, itvari b1 where b2.assort_id=tod.assort_id And b2.item_id= tod.item_id And b2.assort_id = b1.assort_id and b2.variation_gb1 = b1.variation_gb and b2.variation_seq1=b1.seq) optionNm1 , (select b1.option_nm from ititmm b2, itvari b1 where b2.assort_id=tod.assort_id And b2.item_id= tod.item_id And b2.assort_id = b1.assort_id and b2.variation_gb2 = b1.variation_gb and b2.variation_seq2=b1.seq) optionNm2 ,lm.storage_id storageId from lsshpm lm ,lsshpd ld ,itasrt it ,tb_order_detail tod ,tb_order_master tom where 1=1 and lm.receipt_dt between #{startDt} and #{endDt} and lm.ship_status='01' and lm.ship_id = ld.ship_id and ld.assort_id = it.assort_id and ld.order_id = tod.order_id and ld.order_seq = tod.order_seq and tod.status_cd ='C04' and tod.order_id = tom.order_id
and lm.storage_id =#{storageId}
and it.assort_id =#{assortId}
and it.assort_nm like CONCAT( '%', #{assortNm}, '%')
